/* eslint-disable no-undef */
import React from 'react'
import { useState, useEffect } from "react"
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";


const EditUser = () => {
   const { id } = useParams()
   const [user, SetUser] = useState((
      {
         email: '',
         password: ''
      }
   ))

   const [message, SetMessage] = useState();
   const { email, password } = user

   useEffect(() => {
      loaduser();
   },
      []
   )

   const loaduser = async () => {
      const result = await axios.get('http://localhost:4000/user/' + id)
      SetUser(result.data);
   }
   const handleChange = ((e) => {
      SetUser({ ...user, [e.target.name]: e.target.value })
   })

   

   return (
      <><div class="container">
         <div><h2>ADD USER</h2></div>
         <form onSubmit={e => submitForm(e)}>

            <div class="row">
               <div class="col-25">
                  <label for="email">Email</label>
               </div>
               <div class="col-75">
                  <input type="text" id="email" name="email" value={email} onChange={e => handleChange(e)}/>
               </div>
               <div class="col-75">
                  <input type="text" id="password" name="password" value={password} onChange={e => handleChange(e)} hidden/>
               </div>
            </div>

            <div class="row">
               <input type="submit" className='submit' value="Submit" />
            </div>
         </form>
      </div>
      </>
   )
}

export default EditUser
